  if (finalized[_projectId]) revert MIGRATION_TERMINATED();

  // Make sure an amount is specified.
  if (_amount == 0) revert INVALID_AMOUNT();

  // Make sure no ETH was sent.
  if (msg.value > 0) revert NO_MSG_VALUE_ALLOWED();

