    _defaultMetadata,
    _preferAddToBalance,
    _splitsStore.directory(),
    _owner
  )
{
  defaultSplitsProjectId = _defaultSplitsProjectId;
  defaultSplitsDomain = _defaultSplitsDomain;
  defaultSplitsGroup = _defaultSplitsGroup;
  splitsStore = _splitsStore;
}

