query ListProjects($chainId: Int!) {
  projects(
    where: { chainId: $chainId, version: 5 }
    orderBy: "createdAt"
    orderDirection: "desc"
    limit: 20
  ) {
    items {
      projectId
      chainId
      name
      logoUri
      balance
      volume
      tokenSymbol
    }
    totalCount
    pageInfo {
      hasNextPage
      endCursor
    }
  }
}

query GetActivity($projectId: Int!, $chainId: Int!) {
  activityEvents(
    where: { projectId: $projectId, chainId: $chainId, version: 5 }
    orderBy: "timestamp"
    orderDirection: "desc"
    limit: 50
  ) {
    items {
      id
      type
      timestamp
      txHash
      from

      # Include specific event data
      payEvent {
        amount
        amountUsd
        beneficiary
        memo
        newlyIssuedTokenCount
      }
      cashOutTokensEvent {
        cashOutCount
        reclaimAmount
        holder
      }
      mintNftEvent {
        tierId
        tokenId
        totalAmountPaid
      }
    }
    totalCount
  }
}

